const char * test_filename(){
  static const char * s = __FILE__ "        ";
  static char * ret = 0;
  if(!ret){
    ret = strdup(s);
    char * where = ret;
    char * slash = strrchr(ret, '/');
    if(slash) where = slash + 1;
    #ifdef WIN32
    slash = strrchr(ret, '\\');
    if(slash && slash+1 > where)
        where = slash+1;
    #endif // WIN32
    strcpy(where, "Fl_Html_View.html");
  }
  return ret;
}
