void filechooser_callback(Fl_Widget *, void * data){

  const char * file = fl_file_chooser("Choose HTML file", "*.html,*.htm", 0);
  if(file){
    filename = file;
    b->load(filename);
  }
}

void format_callback(Fl_Widget *w, void * data){

  if(((Fl_Check_Button *)w)->value())
    b->format_width(-1);
  else
    b->format_width(0);
  b->redraw();
}


